mapper.select:
SELECT board_id as boardId
FROM appointment
WHERE now() >= appointment_start and appointment_end >= now() and board_id = #{boardId};

select id, user_id, title, hope_area_lat, hope_area_lng, start_day, end_day, appointment_id, share_user_id, not_share_user_id
from sharing_board as s
         right join(
    select *
    from appointment
    where not_share_user_id = #{userId}
) as a
on a.board_id = s.id
where (s.state >= 0 and a.state >= 0)
group by board_id;

SELECT appointment_id, board_id, share_user_id, not_share_user_id, appointment_start, appointment_end
FROM appointment
WHERE board_id = #{boardId};

mapper.insert:
INSERT INTO appointment(board_id, share_user_id, not_share_user_id,
                        appointment_start, appointment_end)
VALUES (#{boardId}, #{shareUserId}, #{notShareUserId}, #{appointmentStart}, #{appointmentEnd});


    SELECT LAST_INSERT_ID()

insert into room (type,board_id,share_user_id,not_share_user_id)
values (#{type},#{boardId}, #{shareUserId},#{notShareUserId});
