mapper.select:
select b.user_id, b.nickname, b.id, b.category, b.hope_area_lng, b.hope_area_lat, b.title, b.address, b.start_day, b.end_day, b.date, b.state, ( case WHEN a.state >= 0 then "요청완료" else "요청중" end ) as type from ask_view as b left join( select * from appointment where type = 1 )as a on b.id = a.board_id
(CAST(hope_area_lat as Double) >= (#{areaLat} - 0.03) and (#{areaLat} + 0.03) >= CAST(hope_area_lat as Double)) AND (CAST(hope_area_lng as Double) >= (#{areaLng} - 0.03) and (#{areaLng} + 0.03) >= CAST(hope_area_lng as Double)) and (now() >= start_day and end_day >= now())
AND (title like #{word} OR content like #{word})
AND category = #{category}
ORDER BY date DESC LIMIT #{low} OFFSET #{high};

mapper.update:
UPDATE ask_board
SET category =#{category} ,title =#{title},content =#{content},
hope_area_lat =#{hopeAreaLat},hope_area_lng =#{hopeAreaLng},start_day = #{startDay},end_day=#{endDay}
WHERE id = #{id};

mapper.insert:

    SELECT LAST_INSERT_ID()

insert into ask_board (user_id,category,title,content,hope_area_lat,
                       hope_area_lng,start_day,end_day, address)
values (#{userId},#{category}, #{title},#{content},#{hopeAreaLat},#{hopeAreaLng}
,#{startDay},#{endDay}, #{address});
