mapper.update:
update user
set password = #{password}
where email = #{email};

update user
set point = point + #{point}
where id = #{userId};

update user
set manner =  manner + #{degree}
where id = #{userId};

mapper.select:
UPDATE user
SET area_lng = #{lng}, area_lat=#{lat}
WHERE id = #{code};

UPDATE user
SET area_lng = #{areaLng}, area_lat=#{areaLat}
WHERE id = #{id};

SELECT email
FROM user
WHERE email = #{email};

SELECT id, email, nickname
FROM user
WHERE id = #{userId} and refresh_token = #{token};

SELECT id, email, password, nickname
FROM user
WHERE nickname = #{nickname};

SELECT nickname,token,point,manner,profile_img, area_lat, area_lng
from user
where id = #{id} and state >= 0;

mapper.update:
